    pulsingActiveColor := getPulsingColor(m.animState.pulsePhase)

    activeBorder := func(w int) lipgloss.Style {
        if w < 2 {
            w = 2
        }
        return lipgloss.NewStyle().
            Border(lipgloss.RoundedBorder()).
            BorderForeground(pulsingActiveColor). // Use pulsing color
            Width(w - 2).
            Height(listHeight - 2)
    }

    // ... rest of renderMatrixView unchanged ...
}
```

