// getFlashColor returns bright flash color based on intensity
func getFlashColor(intensity float64) lipgloss.Color {
    // Intensity: 1.0 (full bright) → 0.0 (normal)

    if intensity <= 0 {
        return lipgloss.Color("") // Use default
    }

    // Very bright magenta for flash
    brightFlash := lipgloss.Color("201") // Bright magenta
    normalActive := ThemeActive

    // For simplicity, return bright if intensity > 0.5, else normal
    // Proper implementation would blend colors
    if intensity > 0.5 {
        return brightFlash
    }
    return normalActive
}
